import React from "react";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import apiRoutes from './apiRoutes';
import '../css/createTask.css';


const CreateTask = () => {
    const [formData, setFormData] = useState({
        titulo: "",
        descripcion: "",
        fecha_vencimiento: "",
        prioridad: "Media",
        estado: "Pendiente",
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createTask(formData);
            navigate("/tasks"); // Redirige a la lista de tareas
        } catch (error) {
            console.error("Error al crear la tarea", error);
        }
    };


    return (
		
		<div className="create-task-page">
			<div className="create-task-container">
				<div className="create-task-form">
				{errores && <p className="error-message">{errores}</p>}
				<h2>Crear Tarea</h2>
					<form>
                        <div className="form-group">
                            <label>T&iacute;tulo</label>
                            <input
                                type="text"
                                id="title"
                                onChange={(e) => setTitle(e.target.value)}
                                value={title}
                                placeholder="Ingresa el t&iacute;tulo de la tarea"/>
                        </div>
                        <div className="form-group">
                            <label>Descripci&oacute;n</label>
                            <input
                                type="text"
                                id="description"
                                onChange={(e) => setDescription(e.target.value)}
                                value={description}
                                placeholder="Ingresa una descripci&oacute;n para la tarea" />
                        </div>
						<div className="form-group">
							<label>Fecha de Vencimiento</label>
							<imput type="date" value={'frontend'} 
							onChange={(e) => setTag(e.target.value)}>
								<input type="date" id="start" name="trip-start" value="2018-07-22" min="2018-01-01" max="2018-12-31" />
							</imput>
						</div>
						<div>
							<label>Prioridad:</label>
							<select name="tag" value={'baja'} 
							onChange={(e) => setPriority(e.target.value)}>
								<option value="baja">Baja</option>
								<option value="media">Media</option>
								<option value="alta">Alta</option>
							</select>
						</div>
						<div>
							<label>Estado:</label>
							<select name="tag" value={'todo'} 
							onChange={(e) => setStatusTask(e.target.value)}>
								<option value="todo">To Do</option>
								<option value="inprogress">In Progress</option>
								<option value="done">Done</option>
							</select>
						</div>
						<button type="button" onClick={validateTask}>
							Crear Tarea
						</button>
					</form>
                    <button type="button" onClick={volver}>Volver</button>
				</div>
            <div className="register-right">
                <div className="image-right"></div>
            </div>
        </div>
    </div>
    );
};

export default CreateTask;
